mapper.insert:
INSERT INTO member(mem_cd, mem_id, mem_pwd, mem_name, mem_email, mem_bir, mem_phone, regdate, mem_addr, mem_addr_detail, mem_zipcode)
			VALUES('M' || TO_char(#{mem_bir}, 'YYYYMMDD') || lpad(mem_seq.nextVal,4,0)
				  ,#{mem_id}
				  ,#{mem_pwd}
				  ,#{mem_name}
				  ,#{mem_email}
				  ,#{mem_bir}
				  ,#{mem_phone}
				  ,SYSDATE
				  ,#{mem_addr}
				  ,#{mem_addr_detail}
				  ,#{mem_zipcode}
					)

mapper.select:
SELECT mem_phone
  FROM member
 WHERE mem_phone = #{mem_phone}

SELECT mem_email
  FROM member
 WHERE mem_email = #{email}
