mapper.select:
SELECT *
FROM movie
WHERE movie_cd = #{movie_cd}

SELECT *
FROM movie_picture
WHERE movie_cd = #{movie_cd}

SELECT *
FROM movie_preview
WHERE movie_cd = #{movie_cd}

SELECT *
FROM moviepost
WHERE movie_cd = #{movie_cd}

SELECT mcd.movie_cd, nvl(reserve, 0) reserve, movie_name, movie_grade, movie_length, movie_mainpic_path, opendate FROM ( SELECT movie_cd, nvl(sum(reserve), 0) reserve FROM ( SELECT count(*) reserve, screen_cd FROM reservation WHERE TO_CHAR(resdate, 'YYYYMMDD') = TO_CHAR(sysdate - 1, 'YYYYMMDD') AND gb_cancel = 'N' GROUP BY screen_cd ) r, screen s WHERE r.screen_cd(+) = s.screen_cd GROUP BY movie_cd ) res, ( SELECT m.movie_cd FROM genre_c gc, movie m WHERE gc.movie_cd = m.movie_cd AND (genre_cd = #{likegenre1}
OR genre_cd = #{likegenre2}
OR genre_cd = #{likegenre3}
) GROUP BY m.movie_cd ) mcd, movie m WHERE res.movie_cd(+) = mcd.movie_cd AND m.movie_cd = mcd.movie_cd AND enddate > SYSDATE AND movie_name like '%' || #{keyword} || '%'
ORDER BY reserve DESC NULLS last
ORDER BY movie_name
ORDER BY opendate

SELECT COUNT(*)
FROM movie_like
WHERE movie_cd = #{movie_cd}
AND mem_cd = #{mem_cd}

mapper.delete:
DELETE movie_like
WHERE movie_cd = #{movie_cd}
AND mem_cd = #{mem_cd}

mapper.select:
SELECT COUNT(*)
FROM movie_like
WHERE movie_cd = #{movie_cd}

SELECT count(*)
FROM review
WHERE mem_cd = #{mem_cd}
AND movie_cd = #{movie_cd}
AND gb_del = 'N'

mapper.delete:
DELETE review_like 
WHERE review_no = #{review_no}
AND mem_cd = #{mem_cd}

mapper.select:
SELECT COUNT(*)
FROM review_like
WHERE mem_cd = #{mem_cd}
AND review_no = #{review_no}

SELECT COUNT(*)
FROM review_report
WHERE review_no = #{review_no}
AND mem_cd = #{mem_cd}

SELECT genre_cd
FROM mem_like_genre
where mem_cd = #{mem_cd}

SELECT r.MERCHANT_UID ,LISTAGG(r.MEM_CAT , ', ') WITHIN GROUP(ORDER BY r.mem_cat) mem_cat ,LISTAGG(r.RES_SEAT , ', ') WITHIN GROUP(ORDER BY RES_SEAT) res_seat ,MAX(startdate) startdate ,MAX(RESDATE) RESDATE ,MAX(h.house_name) house_name ,MAX(h.thr_name) thr_name ,MAX(REFUNDDATE) REFUNDDATE ,MAX(m.movie_name) movie_name ,MAX(m.movie_cd) movie_cd ,MAX(m.movie_mainpic_path) movie_mainpic_path ,MAX(pt.gb_print) gb_print FROM RESERVATION r ,screen s ,movie m ,house h ,PAY_DETAIL p ,photo_ticket pt WHERE r.mem_cd = #{mem_cd}
AND TO_CHAR(startdate, 'YYYYMM') like #{cri.monthKeyword}
AND m.movie_name like '%' || #{cri.keyword} || '%'
AND h.thr_name like '%' || #{cri.keyword} || '%'
AND s.screen_cd = r.screen_cd AND s.movie_cd = m.movie_cd AND h.house_no = s.house_no AND p.merchant_uid = r.merchant_uid AND p.merchant_uid = pt.merchant_uid(+) GROUP BY r.merchant_uid ORDER BY resdate desc

SELECT COUNT(*)
FROM mp_like
WHERE mpost_no = #{mpost_no}
AND mem_cd = #{mem_cd}

SELECT COUNT(*)
FROM reply_report
WHERE mem_cd = #{mem_cd}
AND reply_no = #{reply_no}

SELECT COUNT(*)
FROM mp_report
WHERE mem_cd = #{mem_cd}
AND mpost_no = #{mpost_no}

SELECT COUNT(*) FROM ( SELECT r.MERCHANT_UID ,LISTAGG(r.MEM_CAT , ', ') WITHIN GROUP(ORDER BY r.mem_cat) mem_cat ,LISTAGG(r.RES_SEAT , ', ') WITHIN GROUP(ORDER BY RES_SEAT) res_seat ,MAX(startdate) startdate ,MAX(RESDATE) RESDATE ,MAX(h.house_name) house_name ,MAX(h.thr_name) thr_name ,MAX(REFUNDDATE) REFUNDDATE ,MAX(m.movie_name) movie_name ,MAX(m.movie_cd) movie_cd ,MAX(m.movie_mainpic_path) movie_mainpic_path ,MAX(pt.gb_print) gb_print FROM RESERVATION r ,screen s ,movie m ,house h ,PAY_DETAIL p ,photo_ticket pt WHERE r.mem_cd = #{mem_cd}
AND TO_CHAR(startdate, 'YYYYMM') like #{cri.monthKeyword}
AND m.movie_name like '%' || #{cri.keyword} || '%'
AND h.thr_name like '%' || #{cri.keyword} || '%'
AND s.screen_cd = r.screen_cd AND s.movie_cd = m.movie_cd AND h.house_no = s.house_no AND p.merchant_uid = r.merchant_uid AND p.merchant_uid = pt.merchant_uid(+) GROUP BY r.merchant_uid )

SELECT m.MOVIE_NAME , r.*, mem.MEM_ID, mem.MEM_PIC_PATH, ( SELECT count(*) FROM review_like WHERE REVIEW_NO = r.REVIEW_NO ) reviewlike FROM movie m, review r, MEMBER mem WHERE m.MOVIE_CD = r.MOVIE_CD AND mem.MEM_CD = r.MEM_CD AND r.gb_del = 'N' AND mem.mem_cd = #{mem_cd}
AND movie_name like '%' || #{cri.keyword} ||'%'
AND review_content like '%' || #{cri.keyword} ||'%'

SELECT count(*) FROM ( SELECT m.MOVIE_NAME , r.*, mem.MEM_ID, mem.MEM_PIC_PATH, ( SELECT count(*) FROM review_like WHERE REVIEW_NO = r.REVIEW_NO ) reviewlike FROM movie m, review r, MEMBER mem WHERE m.MOVIE_CD = r.MOVIE_CD AND mem.MEM_CD = r.MEM_CD AND r.gb_del = 'N' AND mem.mem_cd = #{mem_cd}
AND movie_name like '%' || #{cri.keyword} || '%'
AND review_content like '%' || #{cri.keyword} || '%'
)
