mapper.select:
SELECT imp_uid
FROM pay_detail
WHERE merchant_uid = #{merchant_uid}

SELECT paid_amount, refunddate, nvl(card_name, '카카오페이') card_name, card_quota
FROM pay_detail
WHERE merchant_uid = #{merchant_uid}

SELECT nvl(MAX(use_point),0) use_point
FROM mem_buy
WHERE merchant_uid = #{merchant_uid}

SELECT merchant_uid
FROM mem_product
WHERE mem_product_cd = #{mem_product_cd}
