mapper.select:
SELECT * FROM ( SELECT p.*, product_name point_name FROM point p, mem_buy mb, product pd WHERE p.relate_cd = mb.merchant_uid AND pd.product_cd = mb.product_cd AND p.mem_cd = #{mem_cd} UNION ALL SELECT p.*, product_name point_name FROM point p, mem_product mp, product pd WHERE mp.mem_product_cd = p.relate_cd AND mp.product_cd = pd.product_cd AND p.mem_cd = #{mem_cd} UNION ALL SELECT p.*, movie_name point_name FROM point p, screen s, movie m, (SELECT merchant_uid, max(screen_cd) screen_cd FROM reservation WHERE mem_cd = #{mem_cd} GROUP BY merchant_uid) r WHERE p.relate_cd = r.merchant_uid AND r.screen_cd = s.screen_cd AND s.movie_cd = m.movie_cd AND p.mem_cd = #{mem_cd}
AND to_char(regdate, 'yyyy-MM-dd') >= #{searchType}
AND to_char(regdate, 'yyyy-MM-dd') <= #{searchType2}
) ORDER BY regdate DESC

SELECT COUNT(*)
FROM point p
WHERE mem_cd = #{mem_cd}

	AND to_char(regdate, 'yyyy-MM-dd') >= #{searchType}


	AND to_char(regdate, 'yyyy-MM-dd') <= #{searchType2}
