mapper.insert:
INSERT INTO admin
			(
			 admin_cd
			,admin_id
			,admin_pwd
			,thr_name
			)
	 VALUES
			(
			 'T' || (SELECT TO_CHAR(SYSDATE, 'YYYYMMDD') FROM dual) || LPAD(admin_seq.nextval,4,0)
			,#{admin_id}
			,#{admin_pwd}
			,#{thr_name}
			)

	SELECT 'T' || (SELECT TO_CHAR(SYSDATE, 'YYYYMMDD') FROM dual d1) || LPAD(admin_seq.currval,4,0) admin_cd FROM dual d2

