			,movie_length
			,movie_info
			,movie_actor
			,movie_grade
			,opendate
			,enddate				
			) 
	 VALUES (
	 		 #{movie_cd} || (SELECT TO_CHAR(SYSDATE, 'YYYYMMDD') FROM dual) || LPAD(movie_seq.nextval,4,0)
	 		,#{movie_name}
	 		,#{movie_mainpic_path}
			,#{movie_director}
			,#{movie_length}
			,#{movie_info}
			,#{movie_actor}
			,#{movie_grade}
			,#{opendate}
			,#{enddate}
	 		)

	SELECT #{movie_cd} || (SELECT TO_CHAR(SYSDATE, 'YYYYMMDD') FROM dual d1) || LPAD(movie_seq.currval,4,0) movie_cd FROM dual d2

