mapper.select:
SELECT *
FROM (
    SELECT m.*, ROW_NUMBER() OVER (ORDER BY OPENDATE DESC) AS rnum
    FROM movie m
)
WHERE rnum BETWEEN #{start} AND #{end}

SELECT *
  FROM theater
 WHERE admin_cd = #{admin_cd}

mapper.update:
UPDATE theater
   SET thr_info = #{thr_info}
 WHERE admin_cd = #{admin_cd}

mapper.insert:
INSERT INTO house
 VALUES (
 		house_seq.nextval
 	   ,#{thr_name}
 	   ,#{house_name}
 	   ,#{house_row}
 	   ,#{house_column}
 	   ,'N'
 		)

	SELECT house_seq.currval FROM dual

mapper.select:
SELECT *
  FROM house
 WHERE house_no = #{house_no}

UPDATE house
   SET house_name = #{house_name}
      ,house_row = #{house_row}
      ,house_column = #{house_column}
 WHERE house_no = #{house_no}

